import React from 'react';

class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      age: null,
      surname: '',
    };
  }

  render() {
    return (
      <form>
      <h1>Hello {this.state.username} {this.state.age}</h1>
      <h1>{this.state.surname}</h1>
      <p>Enter your name:</p>
      <input
        type='text'
        name='username'
        onChange={this.myChangeHandler}
      />
      <p>Enter your age:</p>
      <input
        type='text'
        name='age'
        onChange={this.myChangeHandler}
      />
      <input
        type='text'
        name='surname'
        onChange={this.myChangeHandler}
      />
      </form>
    );
  }
}
export default Counter;
