      };

  }

  render() { 

    return (

      <div>

        <span  className="btn {this.state.classes} btn-sm ">{this.formatCount() }</span>
        <button className="btn btn-secondary btn-sm">Increment</button>
      </div>

    );
  }
  formatCount = ()=>{
      const { count } = this.state;
